        WHERE BABYN = 4
    ) AS g4
        ON b.RespondentID = g4.RespondentID
    LEFT JOIN (
        SELECT RespondentID, BABY_WEIGHT
        FROM weights
        WHERE BABYN = 1
    ) AS w1
        ON b.RespondentID = w1.RespondentID
    LEFT JOIN (
        SELECT RespondentID, BABY_WEIGHT
        FROM weights
        WHERE BABYN = 2
    ) AS w2
        ON b.RespondentID = w2.RespondentID
    LEFT JOIN (
        SELECT RespondentID, BABY_WEIGHT
        FROM weights
        WHERE BABYN = 3
    ) AS w3
        ON b.RespondentID = w3.RespondentID
    LEFT JOIN (
        SELECT RespondentID, BABY_WEIGHT
        FROM weights
        WHERE BABYN = 4
    ) AS w4
        ON b.RespondentID = w4.RespondentID
    LEFT JOIN (
        SELECT RespondentID, MAX(LIVE) AS LIVE
        FROM residence
        GROUP BY RespondentID
    ) AS L
        ON b.RespondentID = L.RespondentID
    LEFT JOIN (
        SELECT RespondentID, MAX(INCOME) as INCOME
        FROM MOTHER_INCOME
        GROUP BY RespondentID
    ) AS I
        ON b.RespondentID = I.RespondentID
;


